import React,{useEffect, useState} from 'react';
import axios from 'axios';
import AnswerAdd from './AnswerAdd';
import { Toast } from 'bootstrap';

function Create()
{
    const [answerList, setAnswerList] = useState([]);
    const [disableAdd, setDisableAdd] = useState(false);
    const [disableDelete, setDisableDelete] = useState(false);
    useEffect(() =>{
        setAnswerList(answerList.concat(<AnswerAdd key={answerList.length + 1} ansNo={answerList.length + 1}/>));
    },[]);
    const addBtnOnClick = (e)=>{
        e.preventDefault();
        console.log(answerList.length);
        if(answerList.length < 4 )
        {
            setDisableAdd(false);
            setAnswerList(answerList.concat(<AnswerAdd key={answerList.length + 1} ansNo={answerList.length + 1}/>));
            if(answerList.length == 3)setDisableAdd(!disableAdd);
        }
        
    }
    const deleteBtnOnClick = (e)=>{
        e.preventDefault();
        console.log(answerList.length);
        if(answerList.length > 1)
        {
            setDisableDelete(true);
            setAnswerList(answerList.slice(0, answerList.length - 1));
            if(answerList.length == 1)setDisableDelete(!disableDelete);
            if(answerList.length < 4)setDisableAdd(false);
        }
        else{
            setDisableDelete(false);
        }
        
    }
return(
    <div className="d-flex justify-content-end align-items-endd-flex bg-primary vh-100 justify-content-center align-items-center">
    <div className = "w-100 bg-white rounded p-3">
        <table className = "table table-primary">
            <thead>
            <tr><th colSpan="2"><h1 className='text-dark'>Create new question</h1></th></tr>
            </thead>
            <tbody>
                <tr>
                    <td>
                        <label>Question No</label>
                    </td>
                    <td>
                        <input type = "text"/>
                    </td>
                </tr>
                <tr>
                    <td>
                        <label>Question</label>
                    </td>
                    <td>
                        <input type = "multitext"/>
                    </td>
                </tr>
                <tr>
                    <td><label>Question Type</label></td>
                    <td>
                        <div className='d-flex justify-content-center'>
                            <div className='m-2'>
                            <input type='radio' name='type' value = 'C'/>
                            <label>Checkbox</label>
                            </div>
                            
                            <div className='m-2'>
                            <input type='radio' name='type' value = 'R'/>
                            <label>Radio</label>
                            </div>
                        </div>
                    </td>
                </tr>
                <tr>
                    <td colSpan="2">
                        <div>
                        {answerList}
                        <div className='d-flex justify-content-between'>
                                <button className={`btn ${disableDelete?'btn-success':'btn-dark'} w-25`} onClick={deleteBtnOnClick}>Delete</button>
                                <button className={`btn ${!disableAdd?'btn-success':'btn-dark'} w-25`} onClick={addBtnOnClick}>Add</button>
                        </div>
                        </div>
                    </td>
                </tr>
                <tr>
                    <td colSpan="2">
                        <button className='btn btn-success'>Create</button>
                    </td>
                </tr>
            </tbody>
        </table>
    </div>
    </div>);
}

export default Create;
